import React from 'react';
import styled from 'styled-components';
import { xs, sm } from '../../styled-components/device';

const HeroBannerWrapper = styled.div`
  display: flex;
  background-color: transparent;
  padding-top: 40px;
  margin-bottom: 40px;
  ${xs}{
    flex-wrap: wrap;
    flex-direction: column;
  }

  .left-col {
    ${xs} {
      padding: 0 16px;
    }
    a {
      display: inline-block;

      img {
        height: 80px;
        ${sm}{
          height: 100px;
        }
      }
    }
  }
  .right-col {
    flex: 1;

    .form-container {
      width: 320px;
      position: relative;
      background-color: rgba(0, 0, 0, .4);
      border-radius: 10px;
      padding: 96px 24px 32px;
      ${xs} {
        margin: 64px auto 0;
      }
      ${sm} {
        margin-left: auto;
      }

      .ph-number {
        background-color: #FFFFFF;
        color: #bf1e2e;
        min-height: 40px;
        font-size: 22px;
        line-height: 1.09;
        letter-spacing: -0.25px;
        font-weight: 700;
        position: absolute;
        left: -24px;
        right: -24px;
        top: 24px;
        display: flex;
        align-items: center;
        justify-content: center;
        box-shadow: 0 0 16px 0 rgba(0, 0, 0, .5);
      }

      .form-group { 
        margin-bottom: 16px;

        .form-control {
          padding: 8px;
          border-radius: 4px;
          height: 36px;
          background-color: #FFFFFF;
          border-color: #FFFFFF;
          font-size: 15px;
          line-height: 1.2;
          letter-spacing: -0.3px;
          color: #000000;

          &:focus {
            box-shadow: none;
          }

          &::placeholder {
            color: #2b292a;
          }
        }

        textarea.form-control {
          height: auto;
        }
      }

      .btn {
        padding: 6px 24px;
        background-color: #FFFFFF;
        border-color: #FFFFFF;
        font-weight: 600;
      }
    }
  }
`;

const HeroBanner = () => {
  return (
    <HeroBannerWrapper>
      <div className="left-col">
        <a href="/">
          <img src="./images/logo.png" alt="logo" />
        </a>
      </div>
      <div className="right-col">
        <div className="form-container">
          <div className="ph-number">513-318-7498</div>
          <div className="form-group">
            <input className="form-control" name="" placeholder="First Name*" />
          </div>
          <div className="form-group">
            <input className="form-control" name="" placeholder="Last Name*" />
          </div>
          <div className="form-group">
            <input className="form-control" name="" placeholder="Phone Number*" />
          </div>
          <div className="form-group">
            <input className="form-control" name="" placeholder="Email Address*" />
          </div>
          <div className="form-group">
            <input className="form-control" name="" placeholder="Zip Code*" />
          </div>
          <div class="form-group">
            <textarea class="form-control" id="exampleTextarea1" placeholder="Comments:" rows="4"></textarea>
          </div>
          <div className="text-center">
            <button className="btn">Submit</button>
          </div>
        </div>
      </div>
    </HeroBannerWrapper>
  );
};

export default HeroBanner;
