import React from 'react';
import styled from 'styled-components';

const HeroBannerWrapper = styled.div`
  display: flex;
  background-color: transparent;
  padding-top: 40px;

  .left-col {
    width: 60%;

    a {
      display: inline-block;

      img {
        height: 100px;
      }
    }
  }
  .right-col {
    width: 40%;

    .form-container {
      position: relative;
      background-color: rgba(0, 0, 0, .4);
      min-height: 600px;
      border-radius: 10px;
      padding: 80px 24px 24px;

      .ph-number {
        background-color: #FFFFFF;
        color: #bf1e2e;
        min-height: 40px;
        font-size: 22px;
        line-height: 1.09;
        letter-spacing: -0.25px;
        font-weight: 700;
        position: absolute;
        left: -24px;
        right: -24px;
        top: 16px;
        display: flex;
        align-items: center;
        justify-content: center;
        box-shadow: 0 0 16px 0 rgba(0, 0, 0, .5);
      }

      .form-group { 
        margin-bottom: 16px;

        .form-control {
          padding: 8px;
          border-radius: 6px;
          height: 36px;
          background-color: #FFFFFF;
          border-color: #FFFFFF;
          font-size: 15px;
          line-height: 1.2;
          letter-spacing: -0.3px;
          color: #000000;

          &:focus {
            box-shadow: none;
          }

          &::placeholder {
            color: #2b292a;
          }
        }
      }
    }
  }
`;

const HeroBanner = () => {
  return (
    <HeroBannerWrapper>
      <div className="left-col">
        <a href="/">
          <img src="./images/logo.png" alt="logo" />
        </a>
      </div>
      <div className="right-col">
        <div className="form-container">
          <div className="ph-number">513-318-7498</div>
          <div className="form-group">
            <input className="form-control" name="" placeholder="First Name*" />
          </div>
          <div className="form-group">
            <input className="form-control" name="" placeholder="Last Name*" />
          </div>
          <div className="form-group">
            <input className="form-control" name="" placeholder="Phone Number*" />
          </div>
          <div className="form-group">
            <input className="form-control" name="" placeholder="Email Address*" />
          </div>
          <div className="form-group">
            <input className="form-control" name="" placeholder="Zip Code*" />
          </div>
        </div>
      </div>
    </HeroBannerWrapper>
  );
};

export default HeroBanner;
